uint64
sys_open(void)
{ char path[MAXPATH]; int fd, omode; struct file *f; struct inode *ip; int n; // 取参数 argint(1, &omode); if((n = argstr(0, path, MAXPATH)) < 0) return -1; // 表示开始一个事务 begin_op(); if(omode & O_CREATE){ ip = create(path, T_FILE, 0, 0); if(ip == 0){ end_op(); return -1; } } // 不需要新建 else { // 获取目标文件的inode if((ip = namei(path)) == 0){ end_op(); return -1; } ilock(ip); // 目录只能以只读方式打开 if(ip->type == T_DIR && omode != O_RDONLY){ iunlockput(ip); end_op(); return -1; } } // 设备文件 if(ip->type == T_DEVICE && (ip->major < 0 || ip->major >= NDEV)){ iunlockput(ip); end_op(); return -1; } // 分配文件控制块和文件描述符 if((f = filealloc()) == 0 || (fd = fdalloc(f)) < 0){ if(f) fileclose(f); iunlockput(ip); end_op(); return -1; } // 设备文件 if(ip->type == T_DEVICE){ f->type = FD_DEVICE; f->major = ip->major; } // 文件或目录 else { f->type = FD_INODE; f->off = 0; } f->ip = ip; // 非只写 f->readable = !(omode & O_WRONLY); // 只写或可读可写 f->writable = (omode & O_WRONLY) || (omode & O_RDWR); // 删除文件 if((omode & O_TRUNC) && ip->type == T_FILE){ itrunc(ip); } iunlock(ip); // 结束一个事务 end_op(); // 返回文件描述符 return fd;
}

