static struct inode*
create(char *path, short type, short major, short minor)
{ struct inode *ip, *dp; char name[DIRSIZ]; // struct inode* nameiparent(char *path, char *name) // 返回path的父目录的inode和目标文件的name // e.g., path="/etc/apt/config" // dp = inode("/etc/apt/") // name = "config" if((dp = nameiparent(path, name)) == 0) return 0; ilock(dp); // struct inode* dirlookup(struct inode *dp, char *name, uint *poff) // 查询dp目录下name文件的inode if((ip = dirlookup(dp, name, 0)) != 0){ iunlockput(dp); ilock(ip); if(type == T_FILE && (ip->type == T_FILE || ip->type == T_DEVICE)) return ip; iunlockput(ip); return 0; } // 若文件不存在 // 分配inode if((ip = ialloc(dp->dev, type)) == 0){ iunlockput(dp); return 0; } // 设置inode ilock(ip); ip->major = major; ip->minor = minor; ip->nlink = 1; // 将ip写入到磁盘 iupdate(ip); // 目录文件 if(type == T_DIR){ // Create . and .. entries. // No ip->nlink++ for ".": avoid cyclic ref count. if(dirlink(ip, ".", ip->inum) < 0 || dirlink(ip, "..", dp->inum) < 0) goto fail; } // 添加目录项 if(dirlink(dp, name, ip->inum) < 0) goto fail; if(type == T_DIR){ // now that success is guaranteed: dp->nlink++; // for ".." iupdate(dp); } iunlockput(dp); // 未释放ip的锁 return ip; fail: // something went wrong. de-allocate ip. ip->nlink = 0; iupdate(ip); iunlockput(ip); iunlockput(dp); return 0;
}

