void
begin_op(void)
{ acquire(&log.lock); while(1){ // 有系统调用正在 commit if(log.committing){ sleep(&log, &log.lock); // 可能超出 log 大小限制 } else if(log.lh.n + (log.outstanding+1)*MAXOPBLOCKS > LOGSIZE){ // this op might exhaust log space; wait for commit. sleep(&log, &log.lock); } else { log.outstanding += 1; release(&log.lock); break; } }
} // kernel/log.c
void
end_op(void)
{ int do_commit = 0; acquire(&log.lock); log.outstanding -= 1; if(log.committing) panic("log.committing"); // 所有系统调用都已经 end_op() if(log.outstanding == 0){ do_commit = 1; log.committing = 1; } else { // begin_op() may be waiting for log space, // and decrementing log.outstanding has decreased // the amount of reserved space. wakeup(&log); } release(&log.lock); // if log.outstanding != 0 // noting to do if(do_commit){ // call commit w/o holding locks, since not allowed // to sleep with locks. commit(); acquire(&log.lock); log.committing = 0; wakeup(&log); release(&log.lock); }
}

